/**
 * ConfC's options.
 *
 * @interface ConfCOptions
 */
interface ConfCOptions {
	/**
	 * Path to configuration files.
	 *
	 * @type {string}
	 * @memberof ConfCOptions
	 */
	path?: string;
	/**
	 * Force to overwrite.
	 *
	 * @type {boolean}
	 * @memberof ConfCOptions
	 */
	overwrite?: boolean;
}
